import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import Graphs from "./Graphs";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      config: require("./../config.json"),
      timesLoaded: false,
      dataLoaded: false,
      times: [],
      data: [],
    };
  }

  componentDidMount() {
    const socket = socketIOClient(this.state.config.app.host + ":" + this.state.config.app.port);

    //new times.star
    socket.on("newTimes", (item) => {
      console.log(item);

      this.setState({
        timesLoaded: true,
        times: this.state.times.concat([item])
      });
    });

    //new data.star
    socket.on("newData", (item) => {
      this.setState({
        dataLoaded: true,
        data: this.state.data.concat([item]),
      });
    });
  }

  //filter data for graphs
  getXY = () => {
    const { config,times, data } = this.state;
    var keyTimes = config.files[Object.keys(config.files)[0]].value0
    var keyData = config.files[Object.keys(config.files)[1]].value0

    //merge by Key 
    let arr3 = times.map((item, i) => Object.assign({}, item, data[i]));


    //filter times and data
    let merged = [];
    for(let i=0; i<times.length; i++) {
      merged.push({
       ...times[i], 
       ...(data.find((itmInner) => itmInner.keyData === times[i].keyTimes))}
      );
    }

    //console.log(merged);



    //merge

    var result = { x: 0, y: 0 };

    return result;
  }

  render() {
    const { timesLoaded, dataLoaded } = this.state;
    if (!(timesLoaded && dataLoaded)) {
      return <div>Loading...</div>;
    } else {
      return (
        //<NavBar />
        //<Activity />
        <Graphs coord={this.getXY()} />
        //<Table />
        //<div>Hello!!!It is {times._mmsdateAuqired_Value} </div>
      );
    }
  }
}
