import React, { Component } from 'react'
import socketIOClient from "socket.io-client"; 
import Graphs from './Graphs'

export default class App extends Component {

  constructor (props){
    super(props)
    this.state = {
      config: require('./../config.json'),
      timesLoaded: false,
      dataLoaded: false,
      times: [],
      data: []
    }
  }


  componentDidMount(){
    const {config, times, data} = this.state;
    const socket = socketIOClient(config.app.host+":"+config.app.port); 

    //new times.star
    socket.on("newTimes", item => {
        this.setState({
          timesLoaded: true,
          times: times.push(item)
        });
    });

    //new data.star
    socket.on("newData", item => {
      this.setState({
        dataLoaded: true,
        data: data.push(item)
      });
    });

  }

  //filter data for graphs
  getXY = () => {
    const {data, times } = this.state;
    var starObject = config.files[Object.keys(config.files)[0]]
    var keyName = starObject[Object.keys(starObject)[0]]

    const mergeById = (a1, a2, id) =>
      a1.map(itm => ({
          ...a2.find((item) => (item[id] === itm[id]) && item),
          ...itm
      }));

    console.log(mergeById(data, times, keyName));
    return 0

  }


  render() {
    const {timesLoaded, dataLoaded, times } = this.state;
    if (!(timesLoaded && dataLoaded)) {
      return <div>Loading...</div>;
    } else {
      return (
        //<NavBar />
        //<Activity />
        <Graphs coord = {this.getXY()}/> 
        //<Table /> 
        //<div>Hello!!!It is {times._mmsdateAuqired_Value} </div>  
      );
    }
  }
}
