import React, { Component } from "react";
import Plot from "react-plotly.js";

export default class Graphs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      response: true,
      revision: 0,
      line1: {
        x: this.props.coord.x,
        y: this.props.coord.y, 
        name: 'Line 1',
        mode: "markers",
        type: "scatter",
      },
      layout: {
        title: "Title Loading",
        xaxis: {
          type: "date",
          //range: [],
          //tickformat: '%H:%M'
        },
        //autosize: false,
        //height: 300,
      }
    };
  }

  addData = () =>  {
    const { line1} = this.state;
    line1.x = this.props.coord.x
    line1.y = this.props.coord.y
    this.setState({ 
      revision: this.state.revision + 1,

    });
  }

  render() {
    addData()
    return (
      <div>
        {this.state.response ? (
          <Plot
            revision={this.state.revision}
            data={[this.state.line1]}
            layout={this.state.layout}
          />
        ) : (
          <p>Loading...</p>
        )}
      </div>
    );
  }
}
