import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import Graphs from "./Graphs";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      config: require("./../config.json"),
      timesLoaded: false,
      dataLoaded: false,
      times: [],
      data: [],
    };
  }

  componentDidMount() {
    const socket = socketIOClient(this.state.config.app.host + ":" + this.state.config.app.port);

    //new times.star
    socket.on("newTimes", (item) => {
      this.setState({
        timesLoaded: true,
        times: this.state.times.concat([item])
      });
    });

    //new data.star
    socket.on("newData", (item) => {
      this.setState({
        dataLoaded: true,
        data: this.state.data.concat([item]),
      });
    });
  }

  //filter data for graphs
  getXY = () => {
    const { config,times, data } = this.state;
    var key1 = config.files[Object.keys(config.files)[0]].value0
    var key2 = config.files[Object.keys(config.files)[1]].value0

    var sizeTimes = Object.keys(config.files[Object.keys(config.files)[0]]).length //6
    var sizeData = Object.keys(config.files[Object.keys(config.files)[1]]).length //15
    var size = sizeTimes + sizeData - 1

    var authormap = {};
    authors.forEach(function(author) {authormap[author.id] = author;});

    // now do the "join":
    books.forEach(function(book) {
        book.author = authormap[book.author_id];
    });




    var mergedArray = Object.values(({...times, ...data}))
    var filtered = mergedArray.filter (o => Object.keys(o).length === size); 
    //console.log(filtered)

